import classes from './Fruit.module.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { connect } from 'react-redux';
import ProductModel from '../../../components/UI/ProductModel/ProductModel';
import * as actionTypes from '../../../store/actions/actionTypes';

class Fruit extends React.Component {

    /* useEffect(() => {
        axios.get('/products')
        .then
    }, []) */
    something(name, price) {
        console.log("here");
        console.log(this.props.user);
        let product = {
            name: name,
            price: price
        }

        axios.patch(`http://localhost:3005/users/${this.props.user._id}`, { "cart": `${this.props.user.cart.push(product)}`} )
        .then(response => {
            console.log(response);
        })
        .catch(err => {
            console.log(err);
        })


        /* this.props.onAddProduct(product); */
    }


    render() {
        let fruitItems = [];
        for (let i = 0; i < this.props.productsArr.length; i++) {
            if (this.props.productsArr[i].type === "Fruit") {
                fruitItems.push(this.props.productsArr[i]);
            }
        }


        return (
            <div className={classes.Fruit}>
                Hi Im the fruit page
                <div className={classes.FruitContainer}>
                    {fruitItems.map((item, index) => {
                        return <ProductModel name={item.name}
                        type={item.type}
                        price={item.price}
                        key={item._id}
                        add={() => this.something(item.name, item.price)}/>;
                    })}
                </div>
            </div>
        )
    }

}

const mapStateToProps = state => {
    return {
        user: state.activeUser,
        productsArr: state.products
    }
}

/* const mapDispatchToProps = state => {
    return {
        onAddProduct: (product) => dispatchEvent({type: actionTypes.ADD_PRODUCT, product})
    }
} */

export default connect(mapStateToProps)(Fruit);
