import classes from './Produce.module.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { connect } from 'react-redux';
import ProductModel from '../../../components/UI/ProductModel/ProductModel';

class Produce extends React.Component {

    /* useEffect(() => {
        axios.get('/products')
        .then
    }, []) */
    render() {
        let produceItems = [];
        for (let i = 0; i < this.props.productsArr.length; i++) {
            console.log(this.props.productsArr[i].type)
            if (this.props.productsArr[i].type === "produce") {
                produceItems.push(this.props.productsArr[i]);
                console.log(produceItems[0]);
            }
        }


        return (
            <div className={classes.Produce}>
                Hi Im the produce page
                {produceItems.map((item, index) => {
                    return <ProductModel name={item.name}
                    desc={item.description}
                    type={item.type}
                    price={item.price}/>;
                })}
            </div>
        )
    }

}

const mapStateToProps = state => {
    return {
        productsArr: state.products
    }
}

export default connect(mapStateToProps)(Produce);
