import React from 'react';
import classes from './Cart.module.css';
import { connect } from 'react-redux';
import CartItem from '../../../components/UI/CartItem/CartItem';

class Cart extends React.Component {

    addHandler(name, price) {
        let data = {
            prevCart: this.props.user.cart,
            product: {
                name: name,
                price: price
            }
        }
        let updatedAcc = {
            ...this.props.user,
            cart: this.props.user.cart.concat(data.product)
        }

        /* axios.patch(`http://localhost:3005/users/${this.props.user.id}`, { cart: `${this.props.user.cart.push(product)}`} ) */
        axios.patch(`http://localhost:3005/users/${this.props.user.id}/add`, data)
        .then(response => {
            console.log(response);
            this.props.onUpdateCart(updatedAcc);
        })
        .catch(err => {
            console.log(err);
        })
    }

    render() {
        return (
            <div className={classes.Cart}>
                {this.props.user.cart.map(item => {
                    return <CartItem key={Math.random()} name={item.name} price={item.price} add={() => this.addHandler(item.name, item.price)}/>
                })}
            </div>
        )
    }
}

const mapStateToProps = state => {
    return {
        user: state.activeUser
    }
}

export default connect(mapStateToProps)(Cart);
