import classes from './Fruit.module.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { connect } from 'react-redux';
import ProductModel from '../../../components/UI/ProductModel/ProductModel';
import * as actionTypes from '../../../store/actions/actionTypes';

class Fruit extends React.Component {

    /* useEffect(() => {
        axios.get('/products')
        .then
    }, []) */
    addHandler(name, price) {
        console.log("here");
        console.log(this.props.user);
        let data = {
            prevCart: this.props.user.cart,
            product: {
                name: name,
                price: price
            }
        }
        let updatedAcc = {
            ...this.props.user,
            cart: this.props.user.cart.concat(data.product)
        }

        /* axios.patch(`http://localhost:3005/users/${this.props.user.id}`, { cart: `${this.props.user.cart.push(product)}`} ) */
        axios.patch(`http://localhost:3005/users/${this.props.user.id}/add`, data)
        .then(response => {
            console.log(response);
            this.props.onUpdateCart(updatedAcc);
        })
        .catch(err => {
            console.log(err);
        })


        /* this.props.onAddProduct(product); */
    }

    removeHandler(name, price) {
        let data = {
            prevCart: this.props.user.cart,
            product: {
                name: name,
                price: price
            }
        }
        let cartArray = this.props.user.cart;

        let index2 = -1;
        for (let i = 0; i < cartArray.length; i++) {
            if (cartArray[i].name === data.product.name) {
                index2 = i;
            }
        }
        if (index2 >= 0) {
            cartArray.splice(index2, 1);
        }
        console.log(cartArray);

        let updatedAcc = {
            ...this.props.user,
            cart: cartArray
        }
        for (let key in this.props.user.cart) {
            console.log(key);
            if (key.name === data.product.name && key.price === data.product.price) {
                console.log("here");
                //PATCH remove
                axios.patch(`http://localhost:3005/users/${this.props.user.id}/remove`, data)
                .then(response => {
                    console.log(response);
                    this.props.onUpdateCart(updatedAcc);
                })
                .catch(err => {
                    console.log(err);
                })
            }
        }
    }


    render() {
        let fruitItems = [];
        for (let i = 0; i < this.props.productsArr.length; i++) {
            if (this.props.productsArr[i].type === "Fruit") {
                fruitItems.push(this.props.productsArr[i]);
            }
        }


        return (
            <div className={classes.Fruit}>
                Hi Im the fruit page
                <div className={classes.FruitContainer}>
                    {fruitItems.map((item, index) => {
                        return <ProductModel name={item.name}
                        type={item.type}
                        price={item.price}
                        key={item._id}
                        add={() => this.addHandler(item.name, item.price)}
                        remove={() => this.removeHandler(item.name, item.price)}/>;
                    })}
                </div>
            </div>
        )
    }

}

const mapStateToProps = state => {
    return {
        user: state.activeUser,
        productsArr: state.products
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onUpdateCart: (account) => dispatch({type: actionTypes.UPDATE_CART, account})
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Fruit);
