import React from 'react';
import Input from '../../components/UI/Input/Input';
import classes from './Auth.module.css';
import { connect } from 'react-redux';
import * as actions from '../../store/actions/index';
import axios from 'axios';


class Auth extends React.Component {
    state = {
        controls: {
            userName: {
                elementType: 'input',
                elementConfig: {
                    type: 'text',
                    placeholder: 'Your Name'
                },
                value: '',
                validation: {
                    required: true
                },
                valid: false,
                touched: false
            },
            password: {
                elementType: 'input',
                elementConfig: {
                    type: 'password',
                    placeholder: 'Password'
                },
                value: '',
                validation: {
                    required: true,
                    minLength: 6
                },
                valid: false,
                touched: false
            }
        },
        isSignUp: false
    }

    checkValidity(value, rules) {
        let isValid = true;
        if (!rules) {
            return true;
        }

        if (rules.required) {
            isValid = value.trim() !== '' && isValid;
        }

        if (rules.minLength) {
            isValid = value.length >= rules.minLength && isValid
        }

        if (rules.maxLength) {
            isValid = value.length <= rules.maxLength && isValid
        }

        if (rules.isEmail) {
            const pattern = /[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?/;
            isValid = pattern.test(value) && isValid
        }

        if (rules.isNumeric) {
            const pattern = /^\d+$/;
            isValid = pattern.test(value) && isValid
        }

        return isValid;
    }

    inputChangedHandler = (event, controlName) => {
        const updatedControls = {
            ...this.state.controls,
            [controlName]: {
                ...this.state.controls[controlName],
                value: event.target.value,
                valid: this.checkValidity(event.target.value, this.state.controls[controlName].validation),
                touched: true
            }
        };
        this.setState({controls: updatedControls});
    }

    submitHandler = (event) => {
        console.log("working");
        const userData = {
            "userName": `${this.state.controls.userName.value}`,
            "password": `${this.state.controls.password.value
            }`
        }
        if (this.state.isSignUp) {
            axios.post('http://localhost:3005/users/add', userData)
            .then(response => console.log(response))
            .catch(err => {
                console.log(err);
            })
        }
    }

    switchAuthMode() {
        this.setState({isSignUp: !this.state.isSignUp})
    }

    render() {
        const formElementsArray = [];
        for (let key in this.state.controls) {
            formElementsArray.push({
                id: key,
                config: this.state.controls[key]
            });
        }

        const form = formElementsArray.map(formElement => (
            <Input 
                key={formElement.id}
                elementType={formElement.config.elementType}
                elementConfig={formElement.config.elementConfig}
                value={formElement.config.value}
                invalid={!formElement.config.valid}
                shouldValidate={formElement.config.validation}
                touched={formElement.config.touched}
                changed={( event ) => this.inputChangedHandler( event, formElement.id )}/>
        ))

        return (
            <div className={classes.Auth}>
                <form onSubmit={() => this.submitHandler()}>
                    {!this.state.isSignUp ? "Log In" : "Sign Up"}
                    {form}
                    <button>SUBMIT</button>
                    <hr />
                    <button onClick={() => this.switchAuthMode()}>{!this.state.isSignUp ? "Switch to Sign Up" : "Switch to Log On"}</button>
                </form>
            </div>

        )
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onAuth: (name, password) => dispatch(actions.auth(name, password))
    };
};

export default connect(null, mapDispatchToProps)(Auth);
