import * as actionTypes from './actions/actionTypes';

const initialState = {
    products: null,
    activeUser: null
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.INIT_PRODUCTS:
            return {
                ...state,
                products: action.productsArr
            }
        case actionTypes.ACTIVATE_USER:
            return {
                ...state,
                activeUser: action.userData
            }
        case actionTypes.LOGOUT_USER:
            return {
                ...state,
                activeUser: null
            }
        case actionTypes.UPDATE_CART:
            return {
                ...state,
                activeUser: action.account
            }
        /* case actionTypes.ADD_PRODUCT:
            return {
                ...state,
                activeUser: {
                    userName: this.state.activeUser.userName,
                    password: this.state.activeUser.password,
                    orders: this.state.activeUser.orders,
                    cart: this.state.activeUser.cart.concat(action.product)
                }
            } */
    }


    return state;
}

export default reducer;
